css:
.sheet-toolbar {
  border-bottom: 1px solid #2c2c2c;
  box-shadow: none;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: 0 0 0.5px rgba(255, 255, 255, 0.28), 0 1px 1.5px rgba(255, 255, 255, 0.08);
}
.thread-list .list-item:not(.unread):not(.selected):not(.focused) {
  color: #aaa;
}
body.platform-win32 .calendar-toggles .colored-checkbox {
  border-radius: 0;
}
body.platform-win32 .calendar-toggles .colored-checkbox .bg-color {
  border-radius: 0;
}
.mailspring-calendar {
  height: 100%;
  display: flex;
  background: #212121;
}
.mailspring-calendar .calendar-toggles {
  display: flex;
  background-color: #121212;
  border-right: 1px solid #2c2c2c;
  color: rgba(238, 238, 238, 0.8);
}
.mailspring-calendar .calendar-toggles .calendar-toggles-wrap {
  padding: 12px;
  padding-top: 0;
}
.mailspring-calendar .calendar-toggles .colored-checkbox {
  position: relative;
  top: 1px;
  margin-left: 1px;
  display: inline-block;
  border-radius: 3px;
  width: 12px;
  height: 12px;
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.4), inset 0 1px 0 rgba(0, 0, 0, 0.13);
  background: white;
}
.mailspring-calendar .calendar-toggles .colored-checkbox .bg-color {
  width: 100%;
  height: 100%;
  border-radius: 3px;
}
.mailspring-calendar .calendar-toggles label {
  padding-left: 0.4em;
}
.mailspring-calendar .calendar-toggles .toggle-wrap,
.mailspring-calendar .calendar-toggles .account-label {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  margin-top: 2px;
}
.mailspring-calendar .calendar-toggles .account-calendars-wrap:first-child {
  margin-top: 0;
}
.mailspring-calendar .calendar-toggles .account-label {
  cursor: default;
  color: rgba(238, 238, 238, 0.6);
  font-weight: 600;
  font-size: 11.718px;
  margin-top: 9px;
  letter-spacing: -0.2px;
}
.mailspring-calendar .calendar-view {
  height: 100%;
  flex: 1;
  position: relative;
}
.mailspring-calendar .calendar-event {
  position: absolute;
  font-size: 11px;
  line-height: 11px;
  font-weight: 500;
  overflow: hidden;
  text-overflow: ellipsis;
  min-height: 21px;
  border-top: 0;
  border-bottom: 2px solid #212121;
  border-left: 0;
  display: flex;
  cursor: default;
}
.mailspring-calendar .calendar-event .default-header {
  overflow: hidden;
  text-overflow: ellipsis;
  margin: 4px 5px 5px 6px;
  cursor: default;
  opacity: 0.7;
  flex: 1;
}
.mailspring-calendar .calendar-event:before {
  content: '';
  position: absolute;
  width: 2px;
  height: 100%;
  left: 0;
  top: 0;
  background: rgba(0, 0, 0, 0.1);
}
.mailspring-calendar .calendar-event.horizontal {
  white-space: nowrap;
}
.mailspring-calendar .calendar-event.selected {
  background-color: #58b660 !important;
}
.mailspring-calendar .calendar-mouse-handler {
  height: 100%;
  display: flex;
  flex-direction: column;
}
.mailspring-calendar .week-view {
  height: 100%;
}
.mailspring-calendar .week-view .calendar-body-wrap {
  display: flex;
  flex-direction: row;
  position: relative;
  height: 100%;
}
.mailspring-calendar .week-view .calendar-body-wrap .calendar-area-wrap {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow-x: auto;
  overflow-y: hidden;
  position: relative;
}
.mailspring-calendar .week-view .calendar-body-wrap .calendar-area-wrap::-webkit-scrollbar {
  display: none;
}
.mailspring-calendar .week-view .calendar-body-wrap .calendar-legend {
  width: 75px;
  box-shadow: 1px 0 0 rgba(177, 177, 177, 0.15);
  z-index: 2;
}
.mailspring-calendar .week-view .week-header {
  position: relative;
  padding-top: 75px;
  border-bottom: 1px solid #2c2c2c;
  flex-shrink: 0;
}
.mailspring-calendar .week-view .date-labels {
  position: absolute;
  width: 100%;
  top: 0;
  left: 0;
  height: 100%;
  display: flex;
}
.mailspring-calendar .week-view .all-day-events {
  position: relative;
  width: auto;
  z-index: 2;
  overflow: hidden;
}
.mailspring-calendar .week-view .all-day-legend {
  width: 75px;
  position: relative;
}
.mailspring-calendar .week-view .legend-text {
  font-size: 11px;
  position: absolute;
  color: #bfbfbf;
  right: 10px;
}
.mailspring-calendar .week-view .date-label-legend {
  width: 75px;
  position: relative;
  border-bottom: 1px solid #dddddd;
  box-shadow: 0 1px 2.5px rgba(0, 0, 0, 0.15);
  z-index: 3;
}
.mailspring-calendar .week-view .date-label-legend .legend-text {
  bottom: 7px;
}
.mailspring-calendar .week-view .day-label-wrap {
  padding: 15px;
  text-align: center;
  flex: 1;
  box-shadow: inset 1px 0 0 rgba(177, 177, 177, 0.15);
}
.mailspring-calendar .week-view .day-label-wrap.is-today .date-label {
  color: #58b660;
}
.mailspring-calendar .week-view .day-label-wrap.is-today .weekday-label {
  color: #58b660;
}
.mailspring-calendar .week-view .date-label {
  display: block;
  font-size: 16px;
  font-weight: 300;
  color: #808080;
}
.mailspring-calendar .week-view .weekday-label {
  display: block;
  font-weight: 500;
  text-transform: uppercase;
  margin-top: 3px;
  font-size: 12px;
  color: #ccd4d8;
}
.mailspring-calendar .week-view .event-grid-wrap {
  flex: 1;
  overflow: auto;
  background: #212121;
  box-shadow: inset 0 1px 2.5px rgba(0, 0, 0, 0.15);
  height: 100%;
}
.mailspring-calendar .week-view .event-grid {
  display: flex;
  position: relative;
}
.mailspring-calendar .week-view .event-grid-legend-wrap {
  overflow: hidden;
  box-shadow: 1px 0 0 rgba(177, 177, 177, 0.15);
}
.mailspring-calendar .week-view .event-grid-legend {
  position: relative;
  background: #212121;
  z-index: 2;
}
.mailspring-calendar .week-view .event-grid-bg-wrap {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 0;
}
.mailspring-calendar .week-view .event-grid-bg-wrap .event-grid-bg {
  position: absolute;
  top: 0;
  left: 0;
}
.mailspring-calendar .week-view .event-grid-bg-wrap .cursor {
  background: rgba(0, 0, 0, 0.04);
  position: absolute;
}
.mailspring-calendar .week-view .event-column {
  flex: 1;
  position: relative;
  height: 100%;
  z-index: 1;
  overflow: hidden;
  box-shadow: 1px 0 0 rgba(177, 177, 177, 0.15);
}
.mailspring-calendar .week-view .event-column.weekend {
  background: rgba(0, 0, 0, 0.02);
}
.mailspring-calendar .current-time-indicator {
  position: absolute;
  width: 100%;
  height: 1px;
  opacity: 0;
  border-top: 1px solid #ff6464;
  z-index: 2;
  transition: opacity ease-in-out 300ms;
}
.mailspring-calendar .current-time-indicator.visible {
  opacity: 1;
}
.mailspring-calendar .current-time-indicator div {
  position: absolute;
  width: 11px;
  height: 11px;
  border-radius: 6px;
  background-color: #ff6464;
  transform: translate3d(-75%, -50%, 0);
  border: 1px solid #212121;
}
.mailspring-calendar .top-banner {
  color: rgba(33, 99, 146, 0.6);
  background: #e0eff6;
  font-size: 12px;
  line-height: 25px;
  text-align: center;
  box-shadow: inset 0 -1px 1px rgba(0, 0, 0, 0.07);
}
.mailspring-calendar .header-controls {
  padding: 10px;
  display: flex;
  color: #808080;
  border-bottom: 1px solid #2c2c2c;
  box-shadow: inset 0 -1px 1px rgba(191, 191, 191, 0.12);
  flex-shrink: 0;
}
.mailspring-calendar .header-controls .title {
  display: inline-block;
  width: 275px;
}
.mailspring-calendar .header-controls .title,
.mailspring-calendar .header-controls .btn-icon {
  font-size: 15px;
  line-height: 25px;
}
.mailspring-calendar .header-controls .btn-icon {
  margin: 0;
  height: auto;
}
.mailspring-calendar .header-controls .btn-icon img {
  vertical-align: baseline;
}
.mailspring-calendar .footer-controls {
  padding: 10px;
  min-height: 45px;
  display: flex;
  color: #808080;
  background: #212121;
  border-top: #2c2c2c;
  box-shadow: 0 -3px 16px rgba(0, 0, 0, 0.11);
  z-index: 2;
}
.mailspring-calendar .center-controls {
  text-align: center;
  flex: 1;
  order: 0;
}
.mini-month-view {
  width: 100%;
  height: 100%;
  min-width: 200px;
  min-height: 200px;
  text-align: center;
  display: flex;
  flex-direction: column;
  background: #212121;
  border: 1px solid #2c2c2c;
  border-radius: 3px;
}
.mini-month-view .header {
  display: flex;
  background: #121212;
  padding: 4px 0 3px 0;
  border-bottom: 1px solid #2c2c2c;
}
.mini-month-view .header .month-title {
  padding-top: 3px;
  color: #eee;
  flex: 1;
}
.mini-month-view .header .btn.btn-icon {
  line-height: 27px;
  height: 27px;
  margin-top: -1px;
  margin-right: 0;
}
.mini-month-view .header .btn.btn-icon:active {
  background: transparent;
}
.mini-month-view .legend {
  display: flex;
  padding: 3px 0;
  border-bottom: 1px solid #2c2c2c;
}
.mini-month-view .legend .weekday {
  flex: 1;
}
.mini-month-view .day-grid {
  display: flex;
  flex-direction: column;
  flex: 1;
}
.mini-month-view .day-grid .week {
  flex: 1;
  display: flex;
  min-height: 28px;
}
.mini-month-view .day-grid .day {
  display: flex;
  flex-direction: column;
  justify-content: center;
  flex: 1;
  min-height: 28px;
  color: rgba(238, 238, 238, 0.6);
}
.mini-month-view .day-grid .day.cur-month {
  color: #eee;
}
.mini-month-view .day-grid .day:hover {
  background: rgba(0, 0, 0, 0.05);
  cursor: pointer;
}
.mini-month-view .day-grid .day.today {
  border: 1px solid #58b660;
}
.mini-month-view .day-grid .day.cur-day {
  background: #58b660;
  color: white;
}
.mini-month-view .day-grid .day.cur-day:hover {
  background: #4aaa53;
}
